        const { data: loans, error: loanError } = await supabase.from('equipment_loans').select('*').eq('equipment_id', equipmentId).order('date_of_delivery', { ascending: false });
        const { data: maintenances, error: maintenanceError } = await supabase.from('equipment_maintenance').select('*').eq('equipment_id', equipmentId).order('service_date', { ascending: false });

        if(loanError || maintenanceError) {
             document.querySelector('#modal-container .modal-body').innerHTML = '<p class="text-red-500">Error al cargar el historial.</p>';
             return;
        }

        const historyItems = [
            ...loans.map(item => ({ ...item, type: 'Préstamo', date: item.date_of_delivery })),
            ...maintenances.map(item => ({...item, type: 'Mantenimiento', date: item.service_date}))
        ].sort((a,b) => new Date(b.date) - new Date(a.date));

        let historyHTML = '<ul class="space-y-4">';
